mapper.update:
update order_product
set order_id = #{orderId},
  sku_id = #{skuId},
  product_name = #{productName},
  product_number = #{productNumber},
  product_price = #{productPrice},
  sku_attr = #{skuAttr}
  code = #{code}
where id = #{id}

mapper.select:
select id, order_id, sku_id, product_name, product_number, product_price, sku_attr,code
from order_product
where order_id = #{id}
